# compilation of binary error test parameters
composition_limits_binary_err = (
    co2_h2o_err + co2_n2_err + co_h2o_err + ar_h2o_err + h2_h2o_err + h2o_n2_err + h2o_o2_err + h2_h2o_err + co_h2o_err
)
