# binary systems -- positive tests
co2_co = [
    (1200, 101325, {"CO2": 1.0, "CO": 0.0}),
    (1200, 101325, {"CO2": 0.0, "CO": 1.0}),
    (1200, 101325, {"CO2": 0.5, "CO": 0.5}),
    (1200, 101325, {"CO2": 0.5, "CO": 0.0}),
    (1200, 101325, {"CO2": 0.0, "CO": 0.5}),
    (1200, 101325, {"CO2": 0.25, "CO": 0.25}),
]
co2_h2o = [
    (1200, 101325, {"CO2": 1.0, "H2O": 0.0}),
    (1200, 101325, {"CO2": 0.0, "H2O": 1.0}),
    (1200, 101325, {"CO2": 0.5, "H2O": 0.5}),
    (1200, 101325, {"CO2": 0.5, "H2O": 0.0}),
    (1200, 101325, {"CO2": 0.0, "H2O": 0.5}),
    (1200, 101325, {"CO2": 0.25, "H2O": 0.25}),
]
co_h2o = [
    (1200, 101325, {"CO": 1.0, "H2O": 0.0}),
    (1200, 101325, {"CO": 0.0, "H2O": 1.0}),
    (1200, 101325, {"CO": 0.5, "H2O": 0.5}),
    (1200, 101325, {"CO": 0.5, "H2O": 0.0}),
    (1200, 101325, {"CO": 0.0, "H2O": 0.5}),
    (1200, 101325, {"CO": 0.25, "H2O": 0.25}),
]

# compilation of binary positive test parameters
composition_limits_binary = co2_co + co2_h2o + co_h2o

# binary systems -- error tests
co2_co_err = [
    (1200, 101325, {"CO2": 1.01, "CO": -0.01}),
    (1200, 101325, {"CO2": -0.01, "CO": 1.01}),
    (1200, 101325, {"CO2": 0.51, "CO": 0.51}),
    (1200, 101325, {"CO2": -0.01, "CO": 0.5}),
]
co2_h2o_err = [
    (1200, 101325, {"CO2": 1.01, "H2O": -0.01}),
    (1200, 101325, {"CO2": -0.01, "H2O": 1.01}),
    (1200, 101325, {"CO2": 0.51, "H2O": 0.51}),
    (1200, 101325, {"CO2": -0.01, "H2O": 0.5}),
]
co_h2o_err = [
    (1200, 101325, {"CO": 1.01, "H2O": -0.01}),
    (1200, 101325, {"CO": -0.01, "H2O": 1.01}),
    (1200, 101325, {"CO": 0.51, "H2O": 0.51}),
    (1200, 101325, {"CO": -0.01, "H2O": 0.5}),
]

# compilation of binary error test parameters
composition_limits_binary_err = co2_co_err + co2_h2o_err + co_h2o_err
