query:
mutation containerFqdnUpdateFromList ( $accountId:ID! $updateFqdnContainerFromListInput:UpdateFqdnContainerFromListInput!) {
	container ( accountId:$accountId ) {
		fqdn {
			updateFromList ( input:$updateFqdnContainerFromListInput   ) {
				container {
					id
					name
					description
					size
					audit {
						createdBy
						lastModifiedBy
						lastModifiedAt
						createdAt
					}
				}
			}
		}
	}	
}
