targets.expr:
sum by(source_id) (
  rate(locust_tl_request_metric_success_milliseconds_sum{run_id=~"$run_id", testplan=~"$testplan"}[$__rate_interval])
)
/
(
  sum by(source_id) (
    rate(locust_tl_request_metric_success_milliseconds_count{run_id=~"$run_id", testplan=~"$testplan"}[$__rate_interval])
  ) or vector(1)
)

sum by(source_id) (
