CREATE TABLE IF NOT EXISTS EQUIPMENT_STAGE_JSON_STREAM (
  SERVICE STRING,
  HOST STRING,
  SYSTEM_ID STRING,
  SYSTEM_TYPE STRING,
  OCTETS_RX DOUBLE,
  OCTETS_TX DOUBLE,
  UNICAST_RX DOUBLE,
  UNICAST_TX DOUBLE,
  OP_STATE STRING,
  DISCARDS_TX DOUBLE,
  DISCARDS_RX DOUBLE,
  MULTICAST_RX DOUBLE,
  MULTICAST_TX DOUBLE,
  COLLECTOR_HOST STRING,
  ERRORS_TX DOUBLE,
  ERRORS_RX DOUBLE,
  BROADCAST_TX DOUBLE,
  BROADCAST_RX DOUBLE,
  TASKID STRING,
  ADMIN_STATE STRING,
  CONNECTOR STRING,
  UTILIZATION_RX DOUBLE,
  UTILIZATION_TX DOUBLE,
  NUNICAST_RX DOUBLE,
  NUNICAST_TX DOUBLE,
  TOTAL_PKTS_RX DOUBLE,
  TOTAL_PKTS_TX DOUBLE,
  DISCARDS_RX_PCT DOUBLE,
  DISCARDS_TX_PCT DOUBLE,
  ERRORS_RX_PCT DOUBLE,
  ERRORS_TX_PCT DOUBLE,
  COLLECTION_TS STRING,
  DATETIMEUTC STRING,
  TIME STRING,
  PRIMARY KEY (SERVICE) NOT ENFORCED
) DISTRIBUTED BY HASH(SERVICE) INTO 1 BUCKETS WITH (
  'changelog.mode' = 'append',
  'kafka.retention.time' = '0',
  'kafka.producer.compression.type' = 'snappy',
  'scan.bounded.mode' = 'unbounded',
  'scan.startup.mode' = 'earliest-offset',
  'value.fields-include' = 'all',
  'key.json-registry.schema-context' = '.flink-dev',
  'value.json-registry.schema-context' = '.flink-dev',
  'key.format' = 'json-registry',
  'value.format' = 'json-registry',
  'properties.bootstrap.servers' = 'localhost:9092',
  'format' = 'json'
);