INSERT INTO EQUIPMENT_STAGE_JSON_STREAM
SELECT
  SUBSTRING(recordData,1,INSTR(recordData, ',') -1) AS SERVICE,
  SUBSTRING(recordData,INSTR(recordData,'host=') +5,INSTR(recordData,'host=') +5) AS HOST,
  SUBSTRING(recordData,INSTR(recordData,'system_id=') +10,INSTR(recordData,'system_id=') +10) AS SYSTEM_ID,
  SUBSTRING(recordData,INSTR(recordData,'system_type=') +12,INSTR(recordData,'system_type=') +12) AS SYSTEM_TYPE,
  CAST(SUBSTRING(recordData,INSTR(recordData,'octets_rx=') +12,INSTR(recordData,'octets_rx=') +5) AS DOUBLE) AS OCTETS_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'octets_tx=') +12,INSTR(recordData,'octets_tx=') +5) AS DOUBLE) AS OCTETS_TX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'unicast_rx=') +12,INSTR(recordData,'unicast_rx=') +5) AS DOUBLE) AS UNICAST_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'unicast_tx=') +12,INSTR(recordData,'unicast_tx=') +5) AS DOUBLE) AS UNICAST_TX,
  SUBSTRING(recordData,INSTR(recordData,'op_state=') +9,INSTR(recordData,'op_state=') +9) AS OP_STATE,
