  CAST(SUBSTRING(recordData,INSTR(recordData,'discards_rx=') +12,INSTR(recordData,'discards_rx=') +5) AS DOUBLE) AS DISCARDS_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'multicast_rx=') +13,INSTR(recordData,'multicast_rx=') +5) AS DOUBLE) AS MULTICAST_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'multicast_tx=') +13,INSTR(recordData,'multicast_tx=') +5) AS DOUBLE) AS MULTICAST_TX,
  SUBSTRING(recordData,INSTR(recordData,'collector_host=') +14,INSTR(recordData,'collector_host=') +14) AS COLLECTOR_HOST,
  CAST(SUBSTRING(recordData,INSTR(recordData,'errors_tx=') +10,INSTR(recordData,'errors_tx=') +5) AS DOUBLE) AS ERRORS_TX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'errors_rx=') +10,INSTR(recordData,'errors_rx=') +5) AS DOUBLE) AS ERRORS_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'broadcast_tx=') +13,INSTR(recordData,'broadcast_tx=') +5) AS DOUBLE) AS BROADCAST_TX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'broadcast_rx=') +13,INSTR(recordData,'broadcast_rx=') +5) AS DOUBLE) AS BROADCAST_RX,
