  REPLACE(SUBSTRING(recordData,INSTR(recordData,'connector=') +10,INSTR(recordData,'connector=') +10), '"', '') AS CONNECTOR,
  CAST(SUBSTRING(recordData,INSTR(recordData,'utilization_rx=') +14,INSTR(recordData,'utilization_rx=') +5) AS DOUBLE) AS UTILIZATION_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'utilization_tx=') +14,INSTR(recordData,'utilization_tx=') +5) AS DOUBLE) AS UTILIZATION_TX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'nunicast_rx=') +12,INSTR(recordData,'nunicast_rx=') +5) AS DOUBLE) AS NUNICAST_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'nunicast_tx=') +12,INSTR(recordData,'nunicast_tx=') +5) AS DOUBLE) AS NUNICAST_TX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'total_pkts_rx=') +13,INSTR(recordData,'total_pkts_rx=') +5) AS DOUBLE) AS TOTAL_PKTS_RX,
  CAST(SUBSTRING(recordData,INSTR(recordData,'total_pkts_tx=') +13,INSTR(recordData,'total_pkts_tx=') +5) AS DOUBLE) AS TOTAL_PKTS_TX,
