  CAST(SUBSTRING(recordData,INSTR(recordData,'discards_tx_pct=') +16,INSTR(recordData,'discards_tx_pct=') +5) AS DOUBLE) AS DISCARDS_TX_PCT,
  CAST(SUBSTRING(recordData,INSTR(recordData,'errors_rx_pct=') +13,INSTR(recordData,'errors_rx_pct=') +5) AS DOUBLE) AS ERRORS_RX_PCT,
  CAST(SUBSTRING(recordData,INSTR(recordData,'errors_tx_pct=') +13,INSTR(recordData,'errors_tx_pct=') +5) AS DOUBLE) AS ERRORS_TX_PCT,
  SUBSTRING(recordData,INSTR(recordData,'collection_ts=') +14,INSTR(recordData,'collection_ts=') +14) AS COLLECTION_TS,
  SUBSTRING(recordData,INSTR(recordData,'datetimeutc=') +10,INSTR(recordData,'datetimeutc=') +10) AS DATETIMEUTC,
  SUBSTRING(recordData,INSTR(recordData,'time=') +5,INSTR(recordData,'time=') +5) AS TIME
