SELECT
  SERVICE,
  HOST,
  PORT_ID,
  OCTETS_RX,
  OCTETS_TX,
  UNICAST_RX,
  UNICAST_TX,
  OP_STATE,
  DISCARDS_TX,
  DISCARDS_RX,
  MULTICAST_RX,
  MULTICAST_TX,
  COLLECTOR_HOST,
  ERRORS_TX,
  ERRORS_RX,
  BROADCAST_TX,
  BROADCAST_RX,
  TASK_ID,
  ADMIN_STATE,
  CONNECTOR,
  UTILIZATION_RX,
  UTILIZATION_TX,
  NUNICAST_RX,
  NUNICAST_TX,
  TOTAL_PKTS_RX,
  TOTAL_PKTS_TX,
  DISCARDS_RX_PCT,
  DISCARDS_TX_PCT,
  ERRORS_RX_PCT,
  ERRORS_TX_PCT,
  COLLECTION_TS,
  DATETIMEUTC,
  TIME
