CREATE TABLE IF NOT EXISTS enriched_events (
  event_id STRING NOT NULL PRIMARY KEY NOT ENFORCED,
  user_id STRING,
  event_timestamp TIMESTAMP(3),
  event_type STRING,
  tag_count INT,
  has_premium_tag BOOLEAN,
  individual_tag STRING,
  first_tag STRING,
  device_os STRING,
  device_browser STRING,
  user_country STRING,
  user_city STRING,
  campaign_id STRING,
  traffic_source STRING,
  custom_field_keys ARRAY<STRING>,
  custom_field_values ARRAY<STRING>,
  user_language STRING,
  session_duration STRING,
  tags_concatenated STRING,
  tags_pipe_separated STRING,
  vip_user_country STRING,
  recent_event_types ARRAY<STRING>,
  all_user_countries ARRAY<STRING>
) DISTRIBUTED BY HASH(event_id) INTO 1 BUCKETS WITH (
   'changelog.mode' = 'append',
   'key.avro-registry.schema-context' = '.flink-dev',
   'value.avro-registry.schema-context' = '.flink-dev',
   'key.format' = 'avro-registry',
   'value.format' = 'avro-registry',
   'kafka.retention.time' = '0',
   'kafka.producer.compression.type' = 'snappy',
   'scan.bounded.mode' = 'unbounded',
   'scan.startup.mode' = 'earliest-offset',
   'value.fields-include' = 'all'
)