package plugins.big.vascular.steerable3DFilter;public class FilterResponse {		private ImageVolume response;	private double[][][][] orientation;		public FilterResponse(ImageVolume response, double[][][][] orientation) {		this.response = response;		this.orientation = orientation;	}		public ImageVolume getResponse() {		return response;	}		public double[][][][] getOrientation() {		return orientation;	}}	